    )

    val listCookies:ArrayList<Cookies>
        get(){
            val list = ArrayList<Cookies>()
            for(index in title.indices){
                val cookies = Cookies()
                cookies.idCookies = index+1
                cookies.title = title[index]
                cookies.desc = desc[index]
                cookies.img = img[index]
                cookies.bahan = bahan[index]
                cookies.cara = cara[index]
                cookies.sumber = sumber[index]
                cookies.score = score[index]
                list.add(cookies)
            }
            return list
        }

}